mapper.update:
UPDATE tb_article
SET article_comment_num =article_comment_num + 1
WHERE id = #{articleId}

UPDATE tb_article
SET article_comment_num =article_comment_num - 1
WHERE id = #{articleId}

UPDATE tb_article
SET article_read_count =article_read_count + 1
WHERE id = #{articleId}

UPDATE tb_article
SET article_star_num = article_star_num + 1
WHERE id = #{articleId}

mapper.select:
SELECT ta.*,te.username publishUserName,tat.type_name articleTypeName FROM tb_article ta LEFT JOIN tb_employee te ON ta.publish_id = te.id LEFT JOIN tb_article_type tat ON ta.article_type = tat.id
AND ta.article_name LIKE CONCAT('%',#{keywords},'%')
AND ta.article_state =#{articleState}

SELECT *
FROM ip_address
WHERE article_id = #{articleId}
  AND address = #{ip}
